import React, { Component } from "react";
import { makeStyles, withStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";

import PropTypes from "prop-types";
import { connect } from "react-redux";
import { getPatientLists } from "../../actions/patientlistActions";
import { loginUser } from "../../actions/authActions";

const useCardStyles = theme => ({
  root: {
    minWidth: 275,
    maxWidth: 275,
  },
  bullet: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)",
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
});

class SimpleCard extends Component {
  componentDidMount() {
    this.props.getPatientLists();
  }
  render() {
    const { classes } = this.props;
    const { list } = this.props.patients;
    return (
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {list.data ? (
          <p>{list.data}</p>
        ) : (
          Object.keys(list).map(key => {
            console.log(list[key].disease.toString().split(","));
            Object.keys(list[key].disease.toString().split(",")).map(index => {
              console.log(list[key].disease.toString().split(",")[index]);
            });
            return (
              <Card
                className={classes.root}
                style={{
                  backgroundImage:
                    "linear-gradient(180deg,rgb(12,194,146),rgb(13, 211, 158),rgb(25, 240, 183))",
                  minWidth: 275,
                  maxWidth: 275,
                }}
              >
                <CardContent>
                  <Typography variant='h4' component='h2'>
                    {list[key].name}
                  </Typography>
                  <Typography className={classes.pos}>
                    Disease {list[key].disease}
                    <Typography
                      className={classes.title}
                      color='textSecondary'
                      gutterBottom
                    >
                      Since {list[key].from}
                    </Typography>
                  </Typography>

                  <Typography variant='body2' component='p'>
                    Description {list[key].description}
                    <Typography
                      className={classes.title}
                      color='textSecondary'
                      gutterBottom
                    >
                      Word of the Day
                    </Typography>
                  </Typography>
                </CardContent>
                <CardActions>
                  <button>Write Prescription</button>
                </CardActions>
              </Card>
            );
          })
        )}
      </div>
    );
  }
}
SimpleCard.propTypes = {
  getPatientLists: PropTypes.func.isRequired,
  patients: PropTypes.object.isRequired,
};

const mapStateToProps = state => ({
  patients: state.patients,
});
export default withStyles(useCardStyles)(
  connect(mapStateToProps, { loginUser, getPatientLists })(SimpleCard)
);
