import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Modal from "react-bootstrap/Modal";
import { Link } from "react-router-dom";

import PropTypes from "prop-types";
import { connect } from "react-redux";
import { getAPatient } from "../../actions/patientlistActions";

class Modals extends Component {
  constructor(props) {
    super(props);
    this.state = {
      show: false,
    };
    this.handleShow = this.handleShow.bind(this);
    this.handleClose = this.handleClose.bind(this);
  }
  handleShow() {
    console.log("in handle show");
    this.setState({
      show: true,
    });
  }
  handleClose() {
    console.log("in handle close");
    this.setState({
      show: false,
    });
  }
  componentDidMount() {
    this.props.getAPatient(this.props.match.params.id);
  }
  render() {
    const { data } = this.props.patients;
    return (
      <>
        <Link to='/dashboard' className='btn-flat waves-effect'>
          <i className='material-icons left'>keyboard_backspace</i> Back to
          patient list
        </Link>
        {console.log("id : ",data.disease)}
        <Form style={{ marginLeft: "2rem" }}>
          <Form.Group as={Row} controlId='formPlaintextEmail'>
            <Form.Label column sm='1'>
              Name
            </Form.Label>
            <Col sm='7'>
              <Form.Control
                plaintext
                readOnly
                defaultValue='email@example.com'
              />
            </Col>
          </Form.Group>
          <Form.Group as={Row} controlId='formPlaintextEmail'>
            <Form.Label column sm='1'>
              Since
            </Form.Label>
            <Col sm='7'>
              <Form.Control
                plaintext
                readOnly
                defaultValue='email@example.com'
              />
            </Col>
          </Form.Group>
          <Form.Group as={Row} controlId='formPlaintextEmail'>
            <Form.Label column sm='1'>
              Disease
            </Form.Label>
            <Col sm='7'>
              <Form.Control
                plaintext
                readOnly
                defaultValue='email@example.com'
              />
            </Col>
          </Form.Group>

          <Form.Group as={Row} controlId='formPlaintextEmail'>
            <Form.Label column sm='1'>
              Description
            </Form.Label>
            <Col sm='7'>
              <Form.Control
                plaintext
                readOnly
                defaultValue='email@example.com'
              />
            </Col>
          </Form.Group>
          <Form.Group as={Row} controlId='formPlaintextEmail'>
            <Form.Label column sm='1'>
              Description
            </Form.Label>
            <Col sm='7'>
              <Form.Control as='textarea' rows='3' />
            </Col>
          </Form.Group>
          <Button variant='primary' onClick={this.handleShow}>
            Launch demo modal
          </Button>
        </Form>
        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>QR code</Modal.Body>
          <Modal.Footer>
            <Button variant='secondary' onClick={this.handleClose}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }
}

Modals.propTypes = {
  getAPatient: PropTypes.func.isRequired,
  patients: PropTypes.object.isRequired,
};

const mapStateToProps = state => ({
  patients: state.patients,
});
export default   connect(mapStateToProps, { getAPatient })(Modals)
