import React, { Component } from "react";
import "./login.css";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { loginUser } from "../../actions/authActions";
import classnames from "classnames";

import unlock from "../../images/unlock.svg";
import femaleAvatar from "../../images/femaleAvatar.svg";
class Login extends Component {
  constructor() {
    super();
    this.state = {
      name: "",
      password: "",
      errors: {},
    };
  }
  componentDidMount() {
    // If logged in and user navigates to Login page, should redirect them to dashboard
    if (this.props.auth.isAuthenticated) {
      this.props.history.push("/dashboard");
    }
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.auth.isAuthenticated) {
      this.props.history.push("/dashboard");
    }

    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors,
      });
    }
  }

  onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };

  onSubmit = e => {
    e.preventDefault();

    const userData = {
      email: this.state.name,
      password: this.state.password,
    };

    this.props.loginUser(userData);
  };
  render() {
    const { errors } = this.state;
    return (
      <div>
        <div className='formElement'>
          <div>
            <img className='avatar' src={femaleAvatar} />
          </div>
          <form noValidate onSubmit={this.onSubmit}>
            <div
              className='inputS'
              style={{
                width: "100",
              }}
            >
              <input
                onChange={this.onChange}
                value={this.state.name}
                error={errors.name}
                id='name'
                className={classnames("", {
                  invalid: errors.name || errors.namenotfound,
                })}
              />
              <label htmlFor='name'>name</label>
              <span className='red-text'>
                {errors.name}
                {errors.namenotfound}
              </span>
            </div>
            <br />
            <div className='inputS'>
              <input
                onChange={this.onChange}
                value={this.state.password}
                error={errors.password}
                id='password'
                type='password'
                className={classnames("", {
                  invalid: errors.password || errors.passwordnotfound,
                })}
              />
              <label htmlFor='name'>name</label>
              <span className='red-text'>
                {errors.name}
                {errors.namenotfound}
              </span>
            </div>
            <br />
            <button className='buttonBox' type='submit'>
              <span>Login</span>
            </button>
          </form>
        </div>
        <img className='tree' src={unlock} alt='Tree' />
      </div>
    );
  }
}

Login.propTypes = {
  loginUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired,
};

const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors,
});

export default connect(mapStateToProps, { loginUser })(Login);
